        return 0 <= x < n and 0 <= y < m

    def execute_command(x, y, command):
        if command == 'L':
            return x, y - 1
        elif command == 'R':
            return x, y + 1
        elif command == 'U':
            return x - 1, y
        elif command == 'D':
            return x + 1, y

    def count_executed_commands(start_x, start_y):
        visited = set()
        x, y = start_x, start_y
        count = 0
        while is_valid_move(x, y) and (x, y) not in visited:
