from collections import defaultdict

def calculate_mean(arr):
    return sum(arr) / len(arr)

def count_pairs(t, testcases):
    results = []

    for _ in range(t):
        n = testcases[_][0]
        arr = testcases[_][1]
        mean = calculate_mean(arr)

        diff_count = defaultdict(int)
        for i in range(n):
            for j in range(i+1, n):
                diff = mean * 2 - arr[i] - arr[j]
                diff_count[diff] += 1

        total_pairs = 0
        for diff in diff_count:
            total_pairs += diff_count[diff] * (diff_count[diff] - 1) // 2

        results.append(total_pairs)

    return results

