def maximize_f_a(n, a):
    counter = 0
    indices_to_remove = []

    for i in range(n):
        if a[i] == i + 1:
            counter += 1
        elif a[i] > i + 1:
            indices_to_remove.append(i)

    while indices_to_remove:
        new_a = []
        new_counter = 0
        j = 0

        for i in range(n):
            if j < len(indices_to_remove) and i == indices_to_remove[j]:
                j += 1
            else:
                new_a.append(a[i])
                if a[i] == len(new_a):
                    new_counter += 1

        if new_counter > counter:
            counter = new_counter
            a = new_a
            indices_to_remove = [idx - 1 for idx in indices_to_remove if idx - 1 >= 0]
        else:
            break

    return counter

