def maximize_f_a(n, a):
    def f(a):
        return sum(1 for i in range(len(a)) if a[i] == i + 1)

    indices_to_remove = []
    for i in range(len(a)):
        if a[i] != i + 1:
            indices_to_remove.append(i)

    max_f_a = f(a)
    for i in indices_to_remove:
        new_a = a[:i] + a[i+1:]
        max_f_a = max(max_f_a, f(new_a))

    return max_f_a

