def find_starting_position(board):
    max_moves = 0
    start_row, start_col = 1, 1

    for r in range(len(board)):
        for c in range(len(board[0])):
            moves = 0
            visited = set()
            curr_r, curr_c = r, c

            while (curr_r, curr_c) not in visited:
                visited.add((curr_r, curr_c))
                move = board[curr_r][curr_c]

                if move == 'L':
                    curr_c -= 1
                elif move == 'R':
                    curr_c += 1
                elif move == 'U':
                    curr_r -= 1
                elif move == 'D':
                    curr_r += 1

                moves += 1

            if moves > max_moves:
                max_moves = moves
                start_row, start_col = r + 1, c + 1

