def calculate_flower_height(n, days):
    height = 1
    consecutive_no_water = 0

    for i in range(n):
        if days[i] == 1:
            if i > 0 and days[i-1] == 1:
                height += 5
            else:
                height += 1
            consecutive_no_water = 0
        else:
            consecutive_no_water += 1
            if consecutive_no_water == 2:
                return -1

    return height

