from itertools import combinations

def count_ways_to_select_problems(testcases):
    def is_valid_combination(comb):
        topics = set()
        difficulties = set()
        for problem in comb:
            if problem[0] in topics or problem[1] in difficulties:
                return False
            topics.add(problem[0])
            difficulties.add(problem[1])
        return True

    results = []
    for problems in testcases:
        valid_combinations = 0
        for comb in combinations(problems, 3):
            if is_valid_combination(comb):
                valid_combinations += 1
        results.append(valid_combinations)

    return results

if __name__ == "__main__":
    testcases = []
    t = int(input())
    for _ in range(t):
        n = int(input())
        problems = []
        for _ in range(n):
            a, b = map(int, input().split())
            problems.append((a, b))
        testcases.append(problems)

    results = count_ways_to_select_problems(testcases)
    for result in results:
        print(result)

