def parse_input():
    import sys
    input = sys.stdin.read
    data = input().strip().split('\n')

    t = int(data[0])
    test_cases = []
    index = 1

    for _ in range(t):
        n = int(data[index])
        array = list(map(int, data[index + 1].split()))
        test_cases.append((n, array))
        index += 2

    return test_cases

def cyclic_shift(arr, l, r, d):
    segment = arr[l-1:r]
    d = d % len(segment)
    shifted_segment = segment[d:] + segment[:d]
    return arr[:l-1] + shifted_segment + arr[r:]

def is_sorted(arr):
    return all(arr[i] <= arr[i+1] for i in range(len(arr) - 1))

def sort_with_cyclic_shifts(n, array):
    operations = []

    if is_sorted(array):
        return operations

    sorted_array = sorted(array)

    for i in range(n):
        if array[i] != sorted_array[i]:
            for j in range(i+1, n):
                if array[j] == sorted_array[i]:
                    l, r, d = i+1, j+1, j-i
                    array = cyclic_shift(array, l, r, d)
                    operations.append((l, r, d))
                    break

    return operations

def main():
    test_cases = parse_input()
    results = []

    for n, array in test_cases:
        operations = sort_with_cyclic_shifts(n, array)
        results.append((len(operations), operations))

    for k, ops in results:
        print(k)
        for l, r, d in ops:
            print(l, r, d)

if __name__ == "__main__":
    main()

