            for start_col in range(m - 3):
                # Try all possible sizes of the portal
                for a in range(5, n - start_row + 1):
                    for b in range(4, m - start_col + 1):
                        # Calculate the number of operations needed for this sub-rectangle
                        operations = 0

                        # Check the borders
                        for x in range(start_row + 1, start_row + a - 1):
                            if grid[x][start_col] == '0':
                                operations += 1
                            if grid[x][start_col + b - 1] == '0':
                                operations += 1
                        for y in range(start_col + 1, start_col + b - 1):
                            if grid[start_row][y] == '0':
                                operations += 1
                            if grid[start_row + a - 1][y] == '0':
                                operations += 1

                        # Check the inner blocks
                        for x in range(start_row + 1, start_row + a - 1):
                            for y in range(start_col + 1, start_col + b - 1):
                                if grid[x][y] == '1':
                                    operations += 1

                        # Update the minimum operations
                        min_operations = min(min_operations, operations)

        results.append(min_operations)

    return results

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    index = 0
    t = int(data[index])
    index += 1
    test_cases = []

    for _ in range(t):
        n = int(data[index])
        m = int(data[index + 1])
        index += 2
        grid = []
        for _ in range(n):
            grid.append(data[index])
            index += 1
        test_cases.append((n, m, grid))

    results = min_operations_to_portal(test_cases)

    for result in results:
