def parse_input():
    import sys
    input = sys.stdin.read
    data = input().strip().split('\n')

    t = int(data[0])
    test_cases = []
    index = 1

    for _ in range(t):
        n = int(data[index])
        array = list(map(int, data[index + 1].split()))
        test_cases.append((n, array))
        index += 2

    return test_cases

def is_sorted(array):
    return all(array[i] <= array[i + 1] for i in range(len(array) - 1))

def cyclic_shift(array, l, r, d):
    segment = array[l:r+1]
    d = d % len(segment)
    shifted_segment = segment[d:] + segment[:d]
    return array[:l] + shifted_segment + array[r+1:]

def sort_with_cyclic_shifts(n, array):
    actions = []
    sorted_array = sorted(array)

    if is_sorted(array):
        return actions

    for i in range(n):
        if array[i] != sorted_array[i]:
            # Find the correct position of the current element
            correct_pos = sorted_array.index(array[i], i)
            if correct_pos > i:
                # Perform a cyclic shift to move the element to its correct position
                l = i
                r = correct_pos
                d = r - l
                array = cyclic_shift(array, l, r, d)
                actions.append((l + 1, r + 1, d))

    return actions

def format_output(test_cases):
    results = []
    for n, array in test_cases:
        actions = sort_with_cyclic_shifts(n, array)
        results.append(f"{len(actions)}")
        for action in actions:
            results.append(f"{action[0]} {action[1]} {action[2]}")
    return "\n".join(results)

def main():
    test_cases = parse_input()
    output = format_output(test_cases)
    print(output)

if __name__ == "__main__":
    main()

