def cyclic_shift(arr, d):
    n = len(arr)
    return arr[-d:] + arr[:-d]

def elementwise_and(arr1, arr2):
    return [x & y for x, y in zip(arr1, arr2)]

def process_array(n, d, arr):
    steps = 0
    while True:
        shifted_arr = cyclic_shift(arr, d)
        new_arr = elementwise_and(arr, shifted_arr)

        if new_arr == arr:
            return -1

        arr = new_arr
        steps += 1

        if all(x == 0 for x in arr):
            return steps

