def solve():
    input = sys.stdin.read
    data = input().split()

    index = 0
    t = int(data[index])
    index += 1

    results = []

    for _ in range(t):
        n = int(data[index])
        m = int(data[index + 1])
        index += 2

        graph = defaultdict(list)

        for _ in range(m):
            i = int(data[index])
            j = int(data[index + 1])
            c = data[index + 2]
            index += 3

            graph[i].append((j, c))
            graph[j].append((i, c))

        color = {}
        max_imposters = 0
        valid = True

        def bfs(start):
            queue = deque([(start, 0)])
            local_imposters = 0
            local_crewmates = 0

            while queue:
                node, col = queue.popleft()

                if node in color:
                    if color[node] != col:
                        return False, 0, 0
                    continue

                color[node] = col
                if col == 0:
                    local_crewmates += 1
                else:
                    local_imposters += 1

                for neighbor, relation in graph[node]:
                    if relation == 'imposter':
                        queue.append((neighbor, 1 - col))
                    else:
                        queue.append((neighbor, col))

            return True, local_imposters, local_crewmates

        for player in range(1, n + 1):
            if player not in color:
                is_valid, imposters, crewmates = bfs(player)
