    results = []
    for case in test_cases:
        n, a = case
        total_sum = sum(a)
        k = total_sum / n
        target_sum = (n - 2) * k

        count = 0
        value_count = {}

        for value in a:
            if value in value_count:
                value_count[value] += 1
            else:
                value_count[value] = 1

        for i in range(n):
            value_count[a[i]] -= 1
            for j in range(i + 1, n):
