import math

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def prettiness_value(n, arr):
    total_sum = 0
    for i in range(n):
        for j in range(i+1, n):
            total_sum += gcd(arr[i], arr[j]) * gcd(i, j)

    return total_sum % (10**9 + 7)

if __name__ == "__main__":
    n = int(input())
    arr = list(map(int, input().split()))

    result = prettiness_value(n, arr)
    print(result)

