def calculate_pairs(t, testcases):
    result = []

    for _ in range(t):
        n = testcases[_][0]
        arr = testcases[_][1]

        total_sum = sum(arr)
        k = total_sum / n

        count = 0
        for i in range(n):
            for j in range(i+1, n):
                if (total_sum - arr[i] - arr[j]) / (n - 2) == k:
                    count += 1

        result.append(count)

    return result

t = int(input())
testcases = []
for _ in range(t):
    n = int(input())
    arr = list(map(int, input().split()))
    testcases.append((n, arr))

result = calculate_pairs(t, testcases)
for res in result:
    print(res)
