        return 0 <= x < n and 0 <= y < m

    def dfs(x, y, visited):
        visited[x][y] = True
        max_moves = 0

        for dx, dy in directions.values():
            new_x, new_y = x + dx, y + dy
            if is_valid_move(new_x, new_y) and not visited[new_x][new_y] and board[new_x][new_y] == board[x][y]:
                max_moves = max(max_moves, dfs(new_x, new_y, visited))

        visited[x][y] = False
        return max_moves + 1

    max_moves = 0
    start_cell = (1, 1)

    for i in range(n):
        for j in range(m):
            visited = [[False] * m for _ in range(n)]
            moves = dfs(i, j, visited)
            if moves > max_moves:
                max_moves = moves
                start_cell = (i + 1, j + 1)

    return start_cell[0], start_cell[1], max_moves

