def flower_growth(test_cases):
    results = []
    for n, days in test_cases:
        height = 1
        dead = False
        for i in range(n):
            if days[i] == 1:
                if i > 0 and days[i - 1] == 1:
                    height += 5
                else:
                    height += 1
            else:
                if i > 0 and days[i - 1] == 0:
                    dead = True
                    break
        if dead:
            results.append(-1)
        else:
            results.append(height)
    return results

