import sys
from itertools import combinations

def can_divide_students(n, availability):
    # Generate all pairs of different days
    days = range(5)
    for day1, day2 in combinations(days, 2):
        count_day1 = 0
        count_day2 = 0
        both_days = 0

        # Count students available on each day and both days
        for student in availability:
            if student[day1] == 1 and student[day2] == 1:
                both_days += 1
            elif student[day1] == 1:
                count_day1 += 1
            elif student[day2] == 1:
                count_day2 += 1

        # Check if we can split students into two equal groups
        for x in range(both_days + 1):
            if count_day1 + x == n // 2 and count_day2 + (both_days - x) == n // 2:
                return True

    return False

