    for n, days in test_cases:
        height = 1
        flower_died = False

        for i in range(n):
            if days[i] == 1:
                if i > 0 and days[i - 1] == 1:
                    height += 5
                else:
                    height += 1
            else:
                if i > 0 and days[i - 1] == 0:
                    flower_died = True
                    break

        if flower_died:
            results.append(-1)
        else:
            results.append(height)

    return results

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    index = 0
    t = int(data[index])
    index += 1
    test_cases = []

    for _ in range(t):
        n = int(data[index])
        index += 1
        days = list(map(int, data[index:index + n]))
        index += n
        test_cases.append((n, days))

    results = flower_growth(test_cases)

    for result in results:
        print(result)

