def can_erase_string(s):
    count_A = s.count('A')
    count_B = s.count('B')
    count_C = s.count('C')
    # Check if the number of 'B's is equal to the sum of 'A's and 'C's
    return count_B == count_A + count_C

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    t = int(data[0])
    results = []

    for i in range(1, t + 1):
        s = data[i]
        if can_erase_string(s):
            results.append("YES")
        else:
            results.append("NO")

    for result in results:
        print(result)

if __name__ == "__main__":
    main()

