def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    m = int(data[1])

    x = [0] * (n + 1)
    y = [0] * (n + 1)

    index = 2
    for i in range(1, n + 1):
        x[i] = int(data[index])
        y[i] = int(data[index + 1])
        index += 2

    operations = []
    for i in range(m):
        op = int(data[index])
        k = int(data[index + 1])
        operations.append((op, k))
        index += 2

    trains = {}
    maintenance_count = [0] * m

    for day in range(m):
        op, k = operations[day]

        if op == 1:  # Add train
            trains[k] = {'state': 'working', 'days_left': x[k]}
        elif op == 2:  # Remove train
            if k in trains:
                del trains[k]

        # Update states
        for model in list(trains.keys()):
            if trains[model]['days_left'] > 0:
                trains[model]['days_left'] -= 1
            else:
                if trains[model]['state'] == 'working':
                    trains[model]['state'] = 'maintenance'
                    trains[model]['days_left'] = y[model]
                else:
                    trains[model]['state'] = 'working'
                    trains[model]['days_left'] = x[model]

        # Count trains in maintenance
        maintenance_count[day] = sum(1 for model in trains if trains[model]['state'] == 'maintenance')

    for count in maintenance_count:
        print(count)

