    t = int(input())
    results = []

    for _ in range(t):
        keyboard = input()
        word = input()
        time = 0

        # Create a dictionary to map each key to its position on the keyboard
        key_positions = {char: idx for idx, char in enumerate(keyboard)}

        # Calculate the total movement time for typing the word
        for i in range(1, len(word)):
            time += abs(key_positions[word[i]] - key_positions[word[i - 1]])

        results.append(time)

    # Output the results
    for result in results:
        print(result)

