def calculate_mean(arr):
    return sum(arr) / len(arr)

def find_valid_pairs(n, arr):
    mean = calculate_mean(arr)
    count = 0
    for i in range(n):
        for j in range(i + 1, n):
            temp_arr = arr[:i] + arr[i+1:j] + arr[j+1:]
            if calculate_mean(temp_arr) == mean:
                count += 1
    return count

