    # Calculate the maximum integer whose cube is <= n
    max_cube = int(n ** (1/3))

    # Generate all squares and cubes up to n
    for i in range(1, max_square + 1):
        squares.add(i * i)
    for i in range(1, max_cube + 1):
        cubes.add(i * i * i)

