    dp = [1] * n
    for i in range(n):
        for j in range(i):
            if sequence[i] > sequence[j]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

def can_split_sequence(sequence):
    n = len(sequence)
    for i in range(1, 1 << (n - 1)):
        subarrays = []
        last_cut = 0
        for j in range(n - 1):
            if (i & (1 << j)) > 0:
                subarrays.append(sequence[last_cut:j + 1])
                last_cut = j + 1
        subarrays.append(sequence[last_cut:])

        lis_lengths = [find_lis(subarray) for subarray in subarrays]
        if sum(lis_lengths) % 2 == 0:
            return "YES"
    return "NO"

