                directions[i][j] = (0, 1)
            elif board[i][j] == 'D':
                directions[i][j] = (1, 0)
            elif board[i][j] == 'U':
                directions[i][j] = (-1, 0)

    return directions

def max_moves(board, n, m):
    directions = get_directions(board, n, m)
    max_moves = 0
    start_cell = (1, 1)

    for i in range(n):
        for j in range(m):
            visited = set()
            x, y = i, j
            moves = 0

            while (x, y) not in visited:
                visited.add((x, y))
                dx, dy = directions[x][y]
                x, y = x + dx, y + dy

                if x < 0 or x >= n or y < 0 or y >= m:
                    break

                moves += 1

            if moves > max_moves:
                max_moves = moves
                start_cell = (i+1, j+1)

    return start_cell, max_moves

