def minimal_difference(t, test_cases):
    results = []
    for a, b, c in test_cases:
        # Calculate the total duration of all songs
        total_duration = a * 1 + b * 2 + c * 3

        # The problem is a variation of the partition problem
        # We need to find the closest sum to total_duration // 2
        # Using a dynamic programming approach to check possible sums
        max_possible_sum = total_duration // 2
        possible_sums = [False] * (max_possible_sum + 1)
        possible_sums[0] = True

        # Add one-minute songs
        for _ in range(a):
            for j in range(max_possible_sum, 0, -1):
                if j >= 1 and possible_sums[j - 1]:
                    possible_sums[j] = True

        # Add two-minute songs
        for _ in range(b):
            for j in range(max_possible_sum, 1, -1):
                if j >= 2 and possible_sums[j - 2]:
                    possible_sums[j] = True

        # Add three-minute songs
        for _ in range(c):
            for j in range(max_possible_sum, 2, -1):
                if j >= 3 and possible_sums[j - 3]:
                    possible_sums[j] = True

        # Find the closest sum to total_duration // 2
        best_sum = 0
        for j in range(max_possible_sum + 1):
            if possible_sums[j]:
                best_sum = j

        # Calculate the minimal possible difference
        min_difference = abs(total_duration - 2 * best_sum)
        results.append(min_difference)

    return results

