                pair_count[(a, b)] = 0

            topic_count[a] += 1
            difficulty_count[b] += 1
            pair_count[(a, b)] += 1

        # Total ways to choose 3 problems
        total_ways = n * (n - 1) * (n - 2) // 6

        # Ways to choose 3 problems with the same topic
        same_topic_ways = 0
        for count in topic_count.values():
            if count >= 2:
                same_topic_ways += count * (count - 1) // 2 * (n - count)

        # Ways to choose 3 problems with the same difficulty
        same_difficulty_ways = 0
        for count in difficulty_count.values():
            if count >= 2:
                same_difficulty_ways += count * (count - 1) // 2 * (n - count)

        # Ways to choose 3 problems with the same topic and difficulty
        same_pair_ways = 0
        for count in pair_count.values():
            if count >= 2:
                same_pair_ways += count * (count - 1) // 2

        # Valid ways
        valid_ways = total_ways - same_topic_ways - same_difficulty_ways + same_pair_ways
        results.append(valid_ways)

    for result in results:
        print(result)

