        graph = defaultdict(list)

        for _ in range(m):
            i, j, c = data[index].split()
            i, j = int(i), int(j)
            if c == "imposter":
                graph[i].append((j, 1))
                graph[j].append((i, 1))
            else:
                graph[i].append((j, 0))
                graph[j].append((i, 0))
            index += 1

        color = [-1] * (n + 1)

        def bfs(start):
            queue = deque([(start, 0)])
            color[start] = 0
            count = [0, 0]
            count[0] += 1

            while queue:
                node, col = queue.popleft()

                for neighbor, relation in graph[node]:
                    expected_color = col ^ relation
                    if color[neighbor] == -1:
                        color[neighbor] = expected_color
                        count[expected_color] += 1
                        queue.append((neighbor, expected_color))
                    elif color[neighbor] != expected_color:
                        return -1

            return max(count)

        max_imposters = 0
        contradiction = False

        for player in range(1, n + 1):
            if color[player] == -1:
