            i, j = int(i), int(j)
            graph[i].append((j, c))
            graph[j].append((i, c))
            index += 1

        # To store the role of each player
        roles = {}

        def dfs(player, role):
            if player in roles:
                return roles[player] == role
            roles[player] = role
            for neighbor, comment in graph[player]:
                if comment == "imposter":
                    if not dfs(neighbor, 1 - role):
                        return False
                else:  # comment == "crewmate"
                    if not dfs(neighbor, role):
                        return False
            return True

        max_imposters = 0
        for player in range(1, n + 1):
            if player not in roles:
                # Try both roles for the player
                roles.clear()
                if dfs(player, 0):
                    imposters_count_0 = sum(1 for p in roles if roles[p] == 1)
                else:
                    imposters_count_0 = -1

                roles.clear()
                if dfs(player, 1):
                    imposters_count_1 = sum(1 for p in roles if roles[p] == 1)
                else:
                    imposters_count_1 = -1

                if imposters_count_0 == -1 and imposters_count_1 == -1:
                    max_imposters = -1
                    break
                else:
                    max_imposters += max(imposters_count_0, imposters_count_1)

        results.append(max_imposters)

