    import sys
    input = sys.stdin.read
    data = input().splitlines()

    index = 0
    t = int(data[index])
    index += 1
    results = []

    for _ in range(t):
        n, m = map(int, data[index].split())
        index += 1

        from collections import defaultdict, deque

        graph = defaultdict(list)

        for _ in range(m):
            i, j, c = data[index].split()
            i, j = int(i), int(j)
            if c == "imposter":
                graph[i].append((j, 1))
                graph[j].append((i, 1))
            else:
                graph[i].append((j, 0))
                graph[j].append((i, 0))
            index += 1

        roles = [-1] * (n + 1)  # -1 means unvisited, 0 means crewmate, 1 means imposter
        contradiction = False
        max_imposters = 0

        for player in range(1, n + 1):
            if roles[player] == -1:  # unvisited
                queue = deque([(player, 0)])  # start with assuming player is crewmate
                imposters_count = 0
                crewmates_count = 0

                while queue:
                    current, role = queue.popleft()

                    if roles[current] == -1:
                        roles[current] = role
                        if role == 1:
                            imposters_count += 1
                        else:
                            crewmates_count += 1
                    elif roles[current] != role:
                        contradiction = True
                        break

                    for neighbor, relation in graph[current]:
                        if roles[neighbor] == -1:
                            queue.append((neighbor, role ^ relation))
                        elif roles[neighbor] != (role ^ relation):
                            contradiction = True
                            break
                    if contradiction:
                        break

                if contradiction:
                    break
                max_imposters += max(imposters_count, crewmates_count)

        if contradiction:
            results.append(-1)
        else:
            results.append(max_imposters)

    for result in results:
        print(result)

