def find_optimal_starting_position(t, test_cases):
    results = []

    for n, m, s in test_cases:
        # Initialize variables to track the best starting position
        best_r, best_c = 1, 1
        max_commands = 0

        # Iterate over all possible starting positions
        for start_r in range(1, n + 1):
            for start_c in range(1, m + 1):
                r, c = start_r, start_c
                commands_executed = 0

                # Simulate the robot's movement
                for command in s:
                    if command == 'L':
                        c -= 1
                    elif command == 'R':
                        c += 1
                    elif command == 'U':
                        r -= 1
                    elif command == 'D':
                        r += 1

                    # Check if the robot is still within the field
                    if 1 <= r <= n and 1 <= c <= m:
                        commands_executed += 1
                    else:
                        break

                # Update the best starting position if necessary
                if commands_executed > max_commands:
                    max_commands = commands_executed
                    best_r, best_c = start_r, start_c

        # Store the result for the current test case
        results.append((best_r, best_c))

    return results

