import sys

def parse_input():
    input_data = sys.stdin.read().strip().split('\n')
    index = 0
    t = int(input_data[index].strip())
    index += 1
    test_cases = []

    for _ in range(t):
        while not input_data[index].strip():
            index += 1
        n, m = map(int, input_data[index].strip().split())
        index += 1
        board = [input_data[index + i].strip() for i in range(n)]
        index += n
        test_cases.append((n, m, board))

    return test_cases

def simulate_robot(n, m, board):
    directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}

    def move_robot(start_r, start_c):
        visited = set()
        r, c = start_r, start_c
        moves = 0

        while True:
            if (r, c) in visited:
                break
            visited.add((r, c))
            moves += 1
            direction = board[r][c]
            dr, dc = directions[direction]
            r, c = r + dr, c + dc

            if r < 0 or r >= n or c < 0 or c >= m:
                break

        return moves

    max_moves = 0
    best_start = (1, 1)

    for r in range(n):
        for c in range(m):
            moves = move_robot(r, c)
            if moves > max_moves:
                max_moves = moves
                best_start = (r + 1, c + 1)

    return best_start[0], best_start[1], max_moves

def main():
    test_cases = parse_input()
    results = []

    for n, m, board in test_cases:
        result = simulate_robot(n, m, board)
        results.append(result)

    for r, c, d in results:
        print(r, c, d)

if __name__ == "__main__":
    main()

