import sys
import math
from collections import defaultdict

MOD = 10**9 + 7

def calculate_prettiness(n, a):
    # Precompute gcd(i, j) for all pairs (i, j)
    gcd_index = defaultdict(int)
    for i in range(1, n + 1):
        for j in range(1, n + 1):
            gcd_value = math.gcd(i, j)
            gcd_index[gcd_value] += 1

    # Precompute gcd(a_i, a_j) using frequency array
    max_value = max(a)
    frequency = [0] * (max_value + 1)
    for num in a:
        frequency[num] += 1

    gcd_element = defaultdict(int)
    for i in range(1, max_value + 1):
        if frequency[i] > 0:
            for j in range(i, max_value + 1, i):
                if frequency[j] > 0:
                    gcd_value = math.gcd(i, j)
                    gcd_element[gcd_value] += frequency[i] * frequency[j]

    # Calculate prettiness value
    prettiness_value = 0
    for g1 in gcd_index:
        for g2 in gcd_element:
            prettiness_value += gcd_index[g1] * gcd_element[g2] * g1 * g2
            prettiness_value %= MOD

    return prettiness_value

def main():
    input = sys.stdin.read
    data = input().split()
    n = int(data[0])
    a = list(map(int, data[1:n+1]))
    result = calculate_prettiness(n, a)
    print(result)

