def minimal_difference(t, test_cases):
    results = []
    for a, b, c in test_cases:
        # Calculate the total duration of all songs
        total_duration = a * 1 + b * 2 + c * 3

        # The goal is to split the total_duration into two parts with minimal difference
        # This is equivalent to finding a subset of songs that sum up to total_duration // 2
        # or as close as possible to it.

        # We can use a dynamic programming approach to solve this problem
        # dp[x] will be True if we can achieve a sum of x using the songs
        max_possible_sum = total_duration // 2
        dp = [False] * (max_possible_sum + 1)
        dp[0] = True

        # Process one-minute songs
        for _ in range(a):
            for j in range(max_possible_sum, 0, -1):
                if j >= 1:
                    dp[j] = dp[j] or dp[j - 1]

        # Process two-minute songs
        for _ in range(b):
            for j in range(max_possible_sum, 1, -1):
                if j >= 2:
                    dp[j] = dp[j] or dp[j - 2]

        # Process three-minute songs
        for _ in range(c):
            for j in range(max_possible_sum, 2, -1):
                if j >= 3:
                    dp[j] = dp[j] or dp[j - 3]

        # Find the best possible sum we can achieve
        best_sum = 0
        for i in range(max_possible_sum + 1):
            if dp[i]:
                best_sum = i

        # Calculate the minimal difference
        other_sum = total_duration - best_sum
        minimal_diff = abs(other_sum - best_sum)
        results.append(minimal_diff)

    return results

