from itertools import combinations

def count_valid_combinations(testcases):
    results = []

    for problems in testcases:
        valid_combinations = 0
        topics = set()
        difficulties = set()

        for topic, difficulty in problems:
            topics.add(topic)
            difficulties.add(difficulty)

        if len(topics) >= 3:
            valid_combinations += len(list(combinations(topics, 3)))

        if len(difficulties) >= 3:
            valid_combinations += len(list(combinations(difficulties, 3)))

        results.append(valid_combinations)

    return results

