def gcd(x, y):
    while y:
        x, y = y, x % y
    return x

def prettiness_value(n, arr):
    total_sum = 0
    for i in range(n):
        for j in range(n):
            total_sum += gcd(arr[i], arr[j]) * gcd(i+1, j+1)

    return total_sum % (10**9 + 7)

