def min_diff_between_concerts(t, test_cases):
    for i in range(t):
        a, b, c = test_cases[i]
        total_duration = a + 2*b + 3*c
        half_duration = total_duration // 2

        if total_duration % 2 == 0:
            print(0)
        else:
            if a == b == c == 1:
                print(1)
            else:
                if a == b == c:
                    print(0)
                else:
                    if a == 0 or b == 0 or c == 0:
                        print(1)
                    else:
                        if a == 1 or b == 1 or c == 1:
                            print(1)
                        else:
                            if half_duration % 2 == 0:
                                print(0)
                            else:
                                print(1)

