import itertools

def calculate_min_difference(a, b, c):
    total_duration = a + 2*b + 3*c
    half_duration = total_duration // 2

    if a >= half_duration:
        return abs(a - half_duration)

    all_songs = [(1, 'a')] * a + [(2, 'b')] * b + [(3, 'c')] * c
    min_diff = float('inf')

    for i in range(1, len(all_songs) + 1):
        for comb in itertools.combinations(all_songs, i):
            duration = sum(song[0] for song in comb)
            diff = abs(duration - half_duration)
            min_diff = min(min_diff, diff)

    return min_diff

