    import sys
    input = sys.stdin.read
    data = input().split()

    # Parse the input
    index = 0
    n = int(data[index])
    m = int(data[index + 1])
    index += 2

    grid = []
    for _ in range(n):
        grid.append(data[index])
        index += 1

    q = int(data[index])
    index += 1

    queries = []
    for _ in range(q):
        x1 = int(data[index])
        x2 = int(data[index + 1])
        queries.append((x1, x2))
        index += 2

    # Determine exitability of each cell in the grid
    exitable = [[False] * m for _ in range(n)]

    # First row and first column
    for i in range(m):
        if grid[0][i] == '.':
            exitable[0][i] = True
        else:
            break

    for i in range(n):
        if grid[i][0] == '.':
            exitable[i][0] = True
        else:
            break

    # Other cells
    for i in range(1, n):
        for j in range(1, m):
            if grid[i][j] == '.' and (exitable[i-1][j] or exitable[i][j-1]):
                exitable[i][j] = True

    # Precompute determinability for each column
    determinable = [True] * m
    for j in range(1, m):
        for i in range(1, n):
            if exitable[i][j-1] and not exitable[i][j]:
                determinable[j] = False
                break

    # Answer each query
    results = []
    for x1, x2 in queries:
        if all(determinable[x1-1:x2]):
            results.append("YES")
        else:
            results.append("NO")

    # Output results
    sys.stdout.write("\n".join(results) + "\n")

