    n = int(data[index])
    m = int(data[index + 1])
    index += 2

    grid = []
    for _ in range(n):
        grid.append(data[index])
        index += 1

    q = int(data[index])
    index += 1

    queries = []
    for _ in range(q):
        x1 = int(data[index])
        x2 = int(data[index + 1])
        queries.append((x1, x2))
        index += 2

    # Initialize exitability status
    exitable = [[False] * m for _ in range(n)]

    # Fill the exitability array
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.':
                if i == 0 or j == 0:
                    exitable[i][j] = True
                else:
                    exitable[i][j] = exitable[i-1][j] or exitable[i][j-1]

    # Preprocess the grid to find transition columns
    transition = [False] * m
    for j in range(1, m):
        for i in range(n):
            if exitable[i][j] and not exitable[i][j-1]:
                transition[j] = True
                break

    # Answer each query
    results = []
    for x1, x2 in queries:
        if any(transition[x1:x2]):
            results.append("NO")
        else:
            results.append("YES")

    # Output results
    sys.stdout.write("\n".join(results) + "\n")

if __name__ == "__main__":
