def reconstruct_word_from_bigrams(test_cases):
    results = []
    for n, bigrams in test_cases:
        word = bigrams[0][0]  # Start with the first character of the first bigram
        for bigram in bigrams:
            if word[-1] != bigram[0]:
                word += bigram[0]
            word += bigram[1]
        if len(word) < n:
            word += bigrams[-1][1]  # Append the last character of the last bigram if needed
        results.append(word)
    return results

