                if j >= 2:
                    dp[j] = dp[j] or dp[j - 2]

        for _ in range(c):
            for j in range(max_possible_sum, 2, -1):
                if j >= 3:
                    dp[j] = dp[j] or dp[j - 3]

        # Find the largest j such that dp[j] is True
        for j in range(max_possible_sum, -1, -1):
            if dp[j]:
                closest_sum = j
                break

        # The minimal difference is the difference between the two concert durations
        minimal_diff = abs(total_duration - 2 * closest_sum)
        results.append(minimal_diff)

    return results

