def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    # Parse the input
    n, m = map(int, data[0].split())
    titles = data[1:n+1]

    # Define a custom key function for sorting
    def custom_key(title):
        # Create a tuple of characters with custom sorting logic
        return tuple(
            (c if i % 2 == 0 else chr(255 - ord(c)))  # odd-indexed: ascending, even-indexed: descending
            for i, c in enumerate(title)
        )

    # Sort the titles using the custom key
    sorted_indices = sorted(range(n), key=lambda i: custom_key(titles[i]))

    # Output the indices of the sorted book titles
    print(' '.join(str(i + 1) for i in sorted_indices))

if __name__ == "__main__":
    main()
