def process_test_cases(test_cases):
    results = []
    for n, d, a in test_cases:
        if n == 1:
            if a[0] == 0:
                results.append(0)
            else:
                results.append(-1)
            continue

        steps = 0
        while True:
            a_shifted = [a[(i + n - d) % n] for i in range(n)]
            new_a = [a[i] & a_shifted[i] for i in range(n)]

            if new_a == a:
                if any(new_a):
                    results.append(-1)
                else:
                    results.append(steps)
                break

            a = new_a
            steps += 1

    return results

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

