def perform_eversions(arr):
    while True:
        pivot = arr[-1]
        left = [x for x in arr if x <= pivot]
        right = [x for x in arr if x > pivot]
        if left + right == arr:
            break
        arr = left + right
    return len(arr) - len(arr[:-1])

def main():
    t = int(input())
    results = []
    for _ in range(t):
        n = int(input())
        arr = list(map(int, input().split()))
        k = perform_eversions(arr)
        results.append(k)
    for result in results:
        print(result)
