                subarrays.append(arr[start:i + 1])
                start = i + 1
        subarrays.append(arr[start:])

        lis_lengths = [longest_increasing_subsequence(subarray) for subarray in subarrays]
        if sum(lis_lengths) % 2 == 0:
            return True
    return False

