    n = len(arr)
    for mask in range(1, 1 << (n - 1)):
        subarrays = []
        start = 0
        for i in range(n - 1):
            if mask & (1 << i):
                subarrays.append(arr[start:i + 1])
                start = i + 1
        subarrays.append(arr[start:])

        lis_lengths = [find_lis(subarray) for subarray in subarrays]
        if all(lis_lengths[i] % 2 == 0 for i in range(len(lis_lengths))):
            xor_sum = 0
            for length in lis_lengths:
                xor_sum ^= length
            if xor_sum == 0:
                return True
    return False

